import React from 'react';
import { Card } from 'antd';
const { Meta } = Card;


const TeamMemberCard = ({ name, role, photo,bio }) => {
  return (
    <div className="card-container">
      <Card
          hoverable
          style={{
            width: 200,
          }}
            cover={<img src={photo} />}
            title={name}
        >
            <h4>{role}</h4>
            <p>{bio}</p>

      </Card>

    </div>      
  );
};

export default TeamMemberCard;
